import React from 'react';

function PreviousGuesses({ guesses, selectedColors, colorBlindMode }) {
  return guesses.length > 0 ? (
    <div className="previous-guesses" style={{ marginBottom: 10, maxHeight: 130, overflowY: 'auto' }}>
      <strong>Previous Guesses:</strong>
      {guesses.map((g, i) => (
        <div key={i} style={{ display: 'flex', alignItems: 'center', margin: '2px 0' }}>
          {g.code.map((color, idx) => {
            const colorObj = selectedColors.find(c => c.name === color);
            return (
              <span
                key={idx}
                style={{
                  display: 'inline-block',
                  background: colorObj ? colorObj.hex : '#ccc',
                  width: 22,
                  height: 22,
                  borderRadius: '50%',
                  margin: '0 2px',
                  border: '1px solid #888',
                  verticalAlign: 'middle',
                  position: 'relative'
                }}
                title={color}
              >
                {colorBlindMode && colorObj && (
                  <span style={{
                    position: 'absolute',
                    top: 2, left: 2,
                    fontSize: 10,
                    color: '#fff',
                    textShadow: '0 0 2px #000'
                  }}>{colorObj.pattern}</span>
                )}
              </span>
            );
          })}
          <span style={{ marginLeft: 8, fontSize: 13 }}>
            ({g.feedback.correct} correct, {g.feedback.wrongPosition} misplaced)
          </span>
        </div>
      ))}
    </div>
  ) : null;
}
export default PreviousGuesses;
