import React from 'react';
import './GuessPanel.css';

const GuessPanel = ({ currentGuess, onGuessChange, onSubmitGuess }) => {
  const handleInputChange = (e) => {
    const value = e.target.value;
    // Only allow digits and limit to 4 characters
    if (/^\d{0,4}$/.test(value)) {
      onGuessChange(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentGuess.length === 4) {
      onSubmitGuess(currentGuess);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && currentGuess.length === 4) {
      onSubmitGuess(currentGuess);
    }
  };

  const addDigit = (digit) => {
    if (currentGuess.length < 4) {
      onGuessChange(currentGuess + digit);
    }
  };

  const removeDigit = () => {
    onGuessChange(currentGuess.slice(0, -1));
  };

  const clearGuess = () => {
    onGuessChange('');
  };

  return (
    <div className="guess-panel">
      <div className="guess-input-section">
        <h3>Enter your guess:</h3>
        <form onSubmit={handleSubmit} className="guess-form">
          <div className="input-container">
            <input
              type="text"
              value={currentGuess}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="0000"
              maxLength={4}
              className="guess-input"
              autoFocus
            />
            <button
              type="button"
              onClick={clearGuess}
              className="clear-btn"
              disabled={!currentGuess}
            >
              Clear
            </button>
          </div>
          <button
            type="submit"
            className="submit-btn"
            disabled={currentGuess.length !== 4}
          >
            Submit Guess
          </button>
        </form>
      </div>

      <div className="number-pad">
        <h4>Number Pad:</h4>
        <div className="number-grid">
          {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0].map((digit) => (
            <button
              key={digit}
              onClick={() => addDigit(digit.toString())}
              className="number-btn"
              disabled={currentGuess.length >= 4}
            >
              {digit}
            </button>
          ))}
          <button
            onClick={removeDigit}
            className="number-btn backspace-btn"
            disabled={!currentGuess}
          >
            âŒ«
          </button>
        </div>
      </div>

      <div className="guess-status">
        <div className="guess-preview">
          <span>Current guess: </span>
          <span className="preview-display">
            {currentGuess.padEnd(4, '_')}
          </span>
        </div>
        <p className="hint">
          {currentGuess.length < 4 
            ? `Enter ${4 - currentGuess.length} more digit${4 - currentGuess.length !== 1 ? 's' : ''}`
            : 'Ready to submit!'
          }
        </p>
      </div>
    </div>
  );
};

export default GuessPanel;
