import React, { useState } from 'react';
import './App.css';
import Lobby from './components/Lobby';
import GuessPanel from './components/GuessPanel';

function App() {
  const colors = ['red', 'blue', 'green', 'purple', 'yellow', 'orange', 'pink', 'brown'];
  const minCodeLength = 4;
  const maxCodeLength = 7;

  const [gameState, setGameState] = useState('lobby');
  const [playerName, setPlayerName] = useState('');
  const [codeLength, setCodeLength] = useState(4);
  const [difficulty, setDifficulty] = useState('easy'); // 'easy' or 'hard'
  const [currentGuess, setCurrentGuess] = useState(Array(4).fill(''));
  const [targetCode, setTargetCode] = useState([]);
  const [latestFeedback, setLatestFeedback] = useState(null);
  const [attempts, setAttempts] = useState(0);

  const handleStartGame = (name) => {
    setPlayerName(name);
    setGameState('setup');
  };

  const handleSetup = () => {
    // Generate code based on difficulty
    let code;
    if (difficulty === 'easy') {
      // No duplicates
      const shuffled = [...colors].sort(() => 0.5 - Math.random());
      code = shuffled.slice(0, codeLength);
    } else {
      // Duplicates allowed
      code = Array.from({ length: codeLength }, () =>
        colors[Math.floor(Math.random() * colors.length)]
      );
    }
    setTargetCode(code);
    setCurrentGuess(Array(codeLength).fill(''));
    setLatestFeedback(null);
    setAttempts(0);
    setGameState('playing');
  };

  const arraysEqual = (a, b) =>
    a.length === b.length && a.every((v, i) => v === b[i]);

  const generateFeedback = (guess, target) => {
    let correct = 0;
    let wrongPosition = 0;
    const targetUsed = new Array(codeLength).fill(false);
    const guessUsed = new Array(codeLength).fill(false);

    for (let i = 0; i < codeLength; i++) {
      if (guess[i] === target[i]) {
        correct++;
        targetUsed[i] = true;
        guessUsed[i] = true;
      }
    }
    for (let i = 0; i < codeLength; i++) {
      if (!guessUsed[i]) {
        for (let j = 0; j < codeLength; j++) {
          if (!targetUsed[j] && guess[i] === target[j]) {
            wrongPosition++;
            targetUsed[j] = true;
            break;
          }
        }
      }
    }
    return { correct, wrongPosition };
  };

  const submitGuess = (guessArr) => {
    if (guessArr.length !== codeLength || guessArr.some(c => !c)) return;

    const feedback = generateFeedback(guessArr, targetCode);
    setLatestFeedback(feedback);
    setAttempts(prev => prev + 1);

    if (arraysEqual(guessArr, targetCode)) {
      setGameState('finished');
    }
    setCurrentGuess(Array(codeLength).fill(''));
  };

  const resetGame = () => {
    setGameState('lobby');
    setPlayerName('');
    setCurrentGuess(Array(codeLength).fill(''));
    setTargetCode([]);
    setLatestFeedback(null);
    setAttempts(0);
    setCodeLength(4);
    setDifficulty('easy');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸ”’ Block-Lock</h1>
        <p>Crack the color code!</p>
        <div style={{ margin: '1rem 0' }}>
          <span style={{ fontWeight: 600 }}>Colors:</span>
          {colors.map(color => (
            <span
              key={color}
              style={{
                display: 'inline-block',
                background: color,
                width: 22,
                height: 22,
                borderRadius: '50%',
                margin: '0 5px',
                border: '2px solid #444',
                verticalAlign: 'middle',
              }}
              title={color}
            ></span>
          ))}
        </div>
      </header>

      <main className="App-main">
        {gameState === 'lobby' && (
          <Lobby onStartGame={handleStartGame} />
        )}

        {gameState === 'playing' && (
  <div className="game-container">
    <div className="game-info">
      <h2>Welcome, {playerName}!</h2>
      <p>Attempts: {attempts}</p>
      <p>Code Length: {codeLength}</p>
      <p>Difficulty: {difficulty === 'easy' ? 'Easy (no duplicates)' : 'Hard (duplicates allowed)'}</p>
    </div>
    {guesses.length > 0 && (
      <div className="previous-guess" style={{ marginBottom: 10 }}>
        <strong>Previous Guess:</strong>
        <span style={{ marginLeft: 8 }}>
          {guesses[guesses.length - 1].code.map((color, idx) => (
            <span
              key={idx}
              style={{
                display: 'inline-block',
                background: color,
                width: 22,
                height: 22,
                borderRadius: '50%',
                margin: '0 3px',
                border: '1px solid #888',
                verticalAlign: 'middle',
              }}
              title={color}
            ></span>
          ))}
          <span style={{ marginLeft: 8 }}>
            ({guesses[guesses.length - 1].feedback.correct} correct, {guesses[guesses.length - 1].feedback.wrongPosition} misplaced)
          </span>
        </span>
      </div>
    )}
    <GuessPanel
      currentGuess={currentGuess}
      onGuessChange={setCurrentGuess}
      onSubmitGuess={submitGuess}
      colors={colors}
      codeLength={codeLength}
    />
    {latestFeedback && (
      <div style={{ marginTop: 10 }}>
        <strong>Feedback:</strong> {latestFeedback.correct} correct, {latestFeedback.wrongPosition} misplaced
      </div>
    )}
  </div>
)}


        {gameState === 'finished' && (
          <div className="game-finished">
            <h2>ðŸŽ‰ Congratulations, {playerName}!</h2>
            <p>You cracked the code in {attempts} attempts!</p>
            <p>
              The code was:{' '}
              <span>
                {targetCode.map((color, idx) => (
                  <span
                    key={idx}
                    style={{
                      display: 'inline-block',
                      background: color,
                      width: 22,
                      height: 22,
                      borderRadius: '50%',
                      margin: '0 5px',
                      border: '2px solid #444',
                      verticalAlign: 'middle',
                    }}
                    title={color}
                  ></span>
                ))}
              </span>
            </p>
            <button onClick={resetGame} className="play-again-btn">
              Play Again
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
