import React from 'react';

function GuessPanel({
  currentGuess,
  onGuessChange,
  onSubmitGuess,
  colors,
  codeLength,
  colorBlindMode = false
}) {
  // Handle color selection for a peg
  const handleColorSelect = (idx, color) => {
    const nextGuess = [...currentGuess];
    nextGuess[idx] = color;
    onGuessChange(nextGuess);
  };

  // Keyboard navigation: Backspace clears peg
  const handleKeyDown = (e, idx) => {
    if (e.key === 'Backspace' || e.key === 'Delete') {
      handleColorSelect(idx, '');
    }
  };

  // Submit guess if all pegs are filled
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!currentGuess.some(c => !c)) {
      onSubmitGuess(currentGuess);
    }
  };

  // Render the color palette for picking a color
  const renderPalette = (pegIdx) => (
    <div className="color-palette" role="group" aria-label={`Color palette for position ${pegIdx + 1}`}>
      {colors.map(color => (
        <button
          key={color.name}
          type="button"
          className="color-peg"
          style={{
            background: color.hex,
            border: currentGuess[pegIdx] === color.name ? '3px solid #667eea' : '2px solid #333',
            width: 32,
            height: 32,
            borderRadius: 6,
            position: 'relative'
          }}
          aria-label={colorBlindMode ? color.pattern : color.name}
          tabIndex={0}
          onClick={() => handleColorSelect(pegIdx, color.name)}
          onKeyDown={e => {
            if (e.key === 'Enter' || e.key === ' ') handleColorSelect(pegIdx, color.name);
          }}
        >
          {colorBlindMode && (
            <span
              style={{
                position: 'absolute',
                top: 7,
                left: 10,
                fontSize: 13,
                color: '#fff',
                textShadow: '0 0 2px #000'
              }}
            >
              {color.pattern}
            </span>
          )}
        </button>
      ))}
      {/* Clear button */}
      <button
        type="button"
        aria-label="Clear"
        style={{
          width: 32,
          height: 32,
          borderRadius: 6,
          background: '#f4f4f4',
          border: '2px dashed #bbb',
          color: '#bbb',
          fontWeight: 'bold',
          fontSize: 18,
          marginLeft: 6,
          cursor: 'pointer'
        }}
        onClick={() => handleColorSelect(pegIdx, '')}
        onKeyDown={e => {
          if (e.key === 'Enter' || e.key === ' ') handleColorSelect(pegIdx, '');
        }}
      >
        Ã—
      </button>
    </div>
  );

  return (
    <form className="guess-panel guess-form" onSubmit={handleSubmit} autoComplete="off">
      <div className="guess-preview" style={{ marginBottom: 14 }}>
        {Array.from({ length: codeLength }).map((_, idx) => {
          const colorObj = colors.find(c => c.name === currentGuess[idx]);
          return (
            <div key={idx} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <div
                className={colorObj ? 'color-preview-square' : 'color-preview-empty'}
                tabIndex={0}
                style={{
                  background: colorObj ? colorObj.hex : '#eee',
                  border: colorObj ? '2.5px solid #444' : '2.5px dashed #bbb',
                  borderRadius: 6,
                  width: 36,
                  height: 36,
                  position: 'relative',
                  marginBottom: 2,
                  outline: currentGuess[idx] ? 'none' : undefined
                }}
                aria-label={colorObj ? (colorBlindMode ? colorObj.pattern : colorObj.name) : 'Empty'}
                onKeyDown={e => handleKeyDown(e, idx)}
                onClick={() => {}} // for accessibility focus
              >
                {colorBlindMode && colorObj && (
                  <span
                    style={{
                      position: 'absolute',
                      top: 7,
                      left: 11,
                      fontSize: 13,
                      color: '#fff',
                      textShadow: '0 0 2px #000'
                    }}
                  >
                    {colorObj.pattern}
                  </span>
                )}
                {!colorObj && (
                  <span style={{
                    color: '#bbb',
                    fontSize: 20,
                    lineHeight: '36px'
                  }}>?</span>
                )}
              </div>
              {renderPalette(idx)}
            </div>
          );
        })}
      </div>
      <button
        type="submit"
        className="submit-btn"
        disabled={currentGuess.some(c => !c)}
        aria-label="Submit guess"
      >
        Guess
      </button>
    </form>
  );
}

export default GuessPanel;
