import React from 'react';
import ColorPalette from './ColorPalette';
import './GuessPanel.css';

const GuessPanel = ({
  currentGuess,
  onGuessChange,
  onSubmitGuess,
  colors,
  codeLength
}) => {
  const ready = currentGuess.every(c => !!c);

  // Add color to first empty slot
  const handleColorSelect = (color) => {
    const idx = currentGuess.findIndex(c => !c);
    if (idx !== -1) {
      const newGuess = [...currentGuess];
      newGuess[idx] = color;
      onGuessChange(newGuess);
    }
  };

  // Allow clearing slot by clicking
  const handleGuessBlockClick = idx => {
    if (currentGuess[idx]) {
      const newGuess = [...currentGuess];
      newGuess[idx] = '';
      onGuessChange(newGuess);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (ready) onSubmitGuess(currentGuess);
  };

  return (
    <div className="guess-panel">
      <form onSubmit={handleSubmit} className="guess-form" style={{ alignItems: 'center' }}>
        <div className="guess-preview" style={{ marginBottom: 12 }}>
          {currentGuess.map((color, idx) =>
            color ? (
              <span
                key={idx}
                className="color-preview-circle"
                style={{
                  background: color,
                  width: 34,
                  height: 34,
                  borderRadius: '50%',
                  margin: '0 4px',
                  border: '2px solid #444',
                  display: 'inline-block',
                  cursor: 'pointer'
                }}
                title="Click to remove"
                onClick={() => handleGuessBlockClick(idx)}
              ></span>
            ) : (
              <span
                key={idx}
                className="color-preview-empty"
                style={{
                  width: 34,
                  height: 34,
                  borderRadius: '50%',
                  background: '#eee',
                  margin: '0 4px',
                  border: '2px dashed #bbb',
                  display: 'inline-block'
                }}
              ></span>
            )
          )}
        </div>
        <ColorPalette colors={colors} onColorSelect={handleColorSelect} />
        <button
          type="submit"
          className="submit-btn"
          disabled={!ready}
          style={{ marginTop: 16, minWidth: 140 }}
        >
          Submit Guess
        </button>
      </form>
    </div>
  );
};

export default GuessPanel;
