import React from 'react';
import './GuessPanel.css';

const GuessPanel = ({
  currentGuess,
  onGuessChange,
  onSubmitGuess,
  colors = ['red', 'blue', 'green', 'yellow'],
  codeLength = 4,
}) => {
  // currentGuess is now an array of color strings, e.g. ['red','blue','green','yellow']

  const handleColorChange = (idx, color) => {
    const newGuess = [...currentGuess];
    newGuess[idx] = color;
    onGuessChange(newGuess);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentGuess.length === codeLength && currentGuess.every(c => c)) {
      onSubmitGuess(currentGuess);
    }
  };

  const clearGuess = () => {
    onGuessChange(Array(codeLength).fill(''));
  };

  return (
    <div className="guess-panel">
      <div className="guess-input-section">
        <h3>Pick your guess:</h3>
        <form onSubmit={handleSubmit} className="guess-form">
          <div className="input-container">
            {Array.from({ length: codeLength }, (_, idx) => (
              <select
                key={idx}
                value={currentGuess[idx] || ''}
                onChange={e => handleColorChange(idx, e.target.value)}
                className="color-select"
              >
                <option value="" disabled>
                  ?
                </option>
                {colors.map(color => (
                  <option key={color} value={color}>
                    {color.charAt(0).toUpperCase() + color.slice(1)}
                  </option>
                ))}
              </select>
            ))}
            <button
              type="button"
              onClick={clearGuess}
              className="clear-btn"
              disabled={currentGuess.every(c => !c)}
            >
              Clear
            </button>
          </div>
          <button
            type="submit"
            className="submit-btn"
            disabled={currentGuess.length !== codeLength || currentGuess.some(c => !c)}
          >
            Submit Guess
          </button>
        </form>
      </div>
      <div className="guess-status">
        <div className="guess-preview">
          <span>Current guess: </span>
          <span className="preview-display">
            {currentGuess.map((color, idx) =>
              color ? (
                <span
                  key={idx}
                  className="color-preview-circle"
                  style={{
                    background: color,
                    display: 'inline-block',
                    width: 20,
                    height: 20,
                    borderRadius: '50%',
                    margin: '0 2px',
                    border: '1px solid #999',
                  }}
                ></span>
              ) : (
                <span key={idx} className="color-preview-empty">
                  &nbsp;?
                </span>
              )
            )}
          </span>
        </div>
        <p className="hint">
          {currentGuess.filter(Boolean).length < codeLength
            ? `Select ${codeLength - currentGuess.filter(Boolean).length} more color${codeLength - currentGuess.filter(Boolean).length !== 1 ? 's' : ''}`
            : 'Ready to submit!'
          }
        </p>
      </div>
    </div>
  );
};

export default GuessPanel;
