import React, { useState } from 'react';
import './Lobby.css';

const Lobby = ({ onStartGame }) => {
  const [playerName, setPlayerName] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (playerName.trim()) {
      setIsLoading(true);
      // Simulate a brief loading state
      setTimeout(() => {
        onStartGame(playerName.trim());
        setIsLoading(false);
      }, 500);
    }
  };

  return (
    <div className="lobby-container">
      <div className="lobby-card">
        <h2>Welcome to Block-Lock!</h2>
        <p className="lobby-description">
          Test your code-breaking skills! Try to guess the secret 4-digit code.
          You'll get feedback on each guess to help you crack it.
        </p>
        
        <form onSubmit={handleSubmit} className="lobby-form">
          <div className="input-group">
            <label htmlFor="playerName">Enter your name:</label>
            <input
              id="playerName"
              type="text"
              value={playerName}
              onChange={(e) => setPlayerName(e.target.value)}
              placeholder="Your name"
              maxLength={20}
              disabled={isLoading}
            />
          </div>
          
          <button 
            type="submit" 
            className="start-game-btn"
            disabled={!playerName.trim() || isLoading}
          >
            {isLoading ? (
              <span className="loading-spinner"></span>
            ) : (
              'Start Game'
            )}
          </button>
        </form>
        
        <div className="game-rules">
          <h3>How to Play:</h3>
          <ul>
            <li>üéØ Guess the secret 4-digit code</li>
            <li>üî¥ Red dots = correct digits in correct positions</li>
            <li>üü° Yellow dots = correct digits in wrong positions</li>
            <li>‚ö´ Black dots = incorrect digits</li>
            <li>üèÜ Crack the code in as few tries as possible!</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Lobby;
