import React from 'react';

function GuessPanel({
  currentGuess,
  onGuessChange,
  onSubmitGuess,
  colors,
  codeLength,
  colorBlindMode = false
}) {
  // Handle color selection for a peg
  const handleColorSelect = (idx, color) => {
    const nextGuess = [...currentGuess];
    nextGuess[idx] = color;
    onGuessChange(nextGuess);
  };

  // Handle keyboard navigation
  const handleKeyDown = (e, idx) => {
    if (e.key === 'Backspace' || e.key === 'Delete') {
      handleColorSelect(idx, '');
    }
  };

  // Submit guess if all pegs are filled
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!currentGuess.some(c => !c)) {
      onSubmitGuess(currentGuess);
    }
  };

  return (
    <form className="guess-panel" onSubmit={handleSubmit} style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
      {Array.from({ length: codeLength }).map((_, idx) => (
        <div key={idx} className="guess-peg">
          <select
            aria-label={`Select color for position ${idx + 1}`}
            value={currentGuess[idx] || ''}
            onChange={e => handleColorSelect(idx, e.target.value)}
            onKeyDown={e => handleKeyDown(e, idx)}
            style={{
              width: 32,
              height: 32,
              borderRadius: '50%',
              background: currentGuess[idx]
                ? colors.find(c => c.name === currentGuess[idx])?.hex
                : '#eee',
              color: '#222',
              textAlign: 'center',
              fontWeight: 'bold'
            }}
          >
            <option value="">?</option>
            {colors.map(color => (
              <option key={color.name} value={color.name}>
                {colorBlindMode ? color.pattern : color.name.charAt(0).toUpperCase() + color.name.slice(1)}
              </option>
            ))}
          </select>
        </div>
      ))}
      <button
        type="submit"
        disabled={currentGuess.some(c => !c)}
        style={{ marginLeft: 12, padding: '0.5em 1em' }}
        aria-label="Submit guess"
      >
        Guess
      </button>
    </form>
  );
}

export default GuessPanel;
