import React, { useState } from 'react';
import './App.css';
import Lobby from './components/Lobby';
import GameBoard from './components/GameBoard';
import GuessPanel from './components/GuessPanel';

function App() {
  const [gameState, setGameState] = useState('lobby'); // 'lobby', 'playing', 'finished'
  const [playerName, setPlayerName] = useState('');
  const [currentGuess, setCurrentGuess] = useState('');
  const [guesses, setGuesses] = useState([]);
  const [targetCode, setTargetCode] = useState('');

  const startGame = (name) => {
    setPlayerName(name);
    setGameState('playing');
    // Generate a random 4-digit code for the game
    const code = Math.floor(1000 + Math.random() * 9000).toString();
    setTargetCode(code);
    console.log('Target code:', code); // Remove this in production
  };

  const submitGuess = (guess) => {
    if (guess.length !== 4) return;
    
    const newGuess = {
      code: guess,
      feedback: generateFeedback(guess, targetCode),
      timestamp: Date.now()
    };
    
    const updatedGuesses = [...guesses, newGuess];
    setGuesses(updatedGuesses);
    setCurrentGuess('');
    
    // Check if player won
    if (guess === targetCode) {
      setGameState('finished');
    }
  };

  const generateFeedback = (guess, target) => {
    let correct = 0;
    let wrongPosition = 0;
    const targetArr = target.split('');
    const guessArr = guess.split('');
    const targetUsed = new Array(4).fill(false);
    const guessUsed = new Array(4).fill(false);

    // First pass: count correct positions
    for (let i = 0; i < 4; i++) {
      if (guessArr[i] === targetArr[i]) {
        correct++;
        targetUsed[i] = true;
        guessUsed[i] = true;
      }
    }

    // Second pass: count wrong positions
    for (let i = 0; i < 4; i++) {
      if (!guessUsed[i]) {
        for (let j = 0; j < 4; j++) {
          if (!targetUsed[j] && guessArr[i] === targetArr[j]) {
            wrongPosition++;
            targetUsed[j] = true;
            break;
          }
        }
      }
    }

    return { correct, wrongPosition };
  };

  const resetGame = () => {
    setGameState('lobby');
    setPlayerName('');
    setCurrentGuess('');
    setGuesses([]);
    setTargetCode('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸ”’ Block-Lock</h1>
        <p>Crack the 4-digit code!</p>
      </header>

      <main className="App-main">
        {gameState === 'lobby' && (
          <Lobby onStartGame={startGame} />
        )}

        {gameState === 'playing' && (
          <div className="game-container">
            <div className="game-info">
              <h2>Welcome, {playerName}!</h2>
              <p>Attempts: {guesses.length}</p>
            </div>
            
            <GameBoard 
              guesses={guesses} 
              currentGuess={currentGuess}
            />
            
            <GuessPanel
              currentGuess={currentGuess}
              onGuessChange={setCurrentGuess}
              onSubmitGuess={submitGuess}
            />
          </div>
        )}

        {gameState === 'finished' && (
          <div className="game-finished">
            <h2>ðŸŽ‰ Congratulations, {playerName}!</h2>
            <p>You cracked the code in {guesses.length} attempts!</p>
            <p>The code was: <strong>{targetCode}</strong></p>
            <button onClick={resetGame} className="play-again-btn">
              Play Again
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
